// Demo data for Arctic Amplification Dashboard const demoTempData=[{time: 'Day 1',temp:-10},{time: 'Day 2',temp:-5},{time: 'Day 3',temp:-15},{time: 'Day 4',temp: 0},{time: 'Day 5',temp:-8},{time: 'Day 6',temp:-12},{time: 'Day 7',temp:-3}];const demoSurvivalData=[{variability: 'Low',survival: 95},{variability: 'Medium',survival: 80},{variability: 'High',survival: 60},{variability: 'Extreme',survival: 40}];const demoStressData=[{time: 'Day 1',stress: 20},{time: 'Day 2',stress: 35},{time: 'Day 3',stress: 50},{time: 'Day 4',stress: 70},{time: 'Day 5',stress: 45},{time: 'Day 6',stress: 55},{time: 'Day 7',stress: 80}];const demoComparisonData=[{scenario: 'Gradual Warming',survival: 85},{scenario: 'High Variability',survival: 60}];const thresholds=[ 'Critical temperature oscillation threshold: Fluctuations > 10Â°C/day reduce survival by 50%.','Physiological stress peaks above 60% under extreme variability.','Conservation recommendation: Monitor Arctic temperature extremes.','Model implication: Variability has stronger impact than mean warming on population dynamics.' ];document.addEventListener('DOMContentLoaded',()=>{const tempDataDiv=document.getElementById('temp-data');const survivalDataDiv=document.getElementById('survival-data');const stressDataDiv=document.getElementById('stress-data');const comparisonDataDiv=document.getElementById('comparison-data');const thresholdsList=document.getElementById('thresholds-list');// Display temp data tempDataDiv.innerHTML='<h3>Temperature(Â°C)</h3>'+demoTempData.map(d=> `<p>${d.time}: ${d.temp}Â°C</p>`).join('');// Display survival data survivalDataDiv.innerHTML='<h3>Survival Rates(%)</h3>'+demoSurvivalData.map(d=> `<p>${d.variability}: ${d.survival}%</p>`).join('');// Display stress data stressDataDiv.innerHTML='<h3>Stress Levels(%)</h3>'+demoStressData.map(d=> `<p>${d.time}: ${d.stress}%</p>`).join('');// Display comparison data comparisonDataDiv.innerHTML='<h3>Survival Comparison</h3>'+demoComparisonData.map(d=> `<p>${d.scenario}: ${d.survival}%</p>`).join('');// Display thresholds thresholdsList.innerHTML=thresholds.map(t=> `<li>${t}</li>`).join('');// Charts using Chart.js const tempCtx=document.getElementById('temp-chart').getContext('2d');new Chart(tempCtx,{type: 'line',data:{labels: demoTempData.map(d=> d.time),datasets: [{label: 'Temperature(Â°C)',data: demoTempData.map(d=> d.temp),borderColor: '#1976d2',backgroundColor: 'rgba(25,118,210,0.1)',fill: true}]},options:{responsive: true,scales:{y:{beginAtZero: false}}}});const survivalCtx=document.getElementById('survival-chart').getContext('2d');new Chart(survivalCtx,{type: 'bar',data:{labels: demoSurvivalData.map(d=> d.variability),datasets: [{label: 'Survival Rate(%)',data: demoSurvivalData.map(d=> d.survival),backgroundColor: '#42a5f5'}]},options:{responsive: true,scales:{y:{beginAtZero: true,max: 100}}}});const stressCtx=document.getElementById('stress-chart').getContext('2d');new Chart(stressCtx,{type: 'line',data:{labels: demoStressData.map(d=> d.time),datasets: [{label: 'Physiological Stress(%)',data: demoStressData.map(d=> d.stress),borderColor: '#ff5722',backgroundColor: 'rgba(255,87,34,0.1)',fill: true}]},options:{responsive: true,scales:{y:{beginAtZero: true,max: 100}}}});const comparisonCtx=document.getElementById('comparison-chart').getContext('2d');new Chart(comparisonCtx,{type: 'bar',data:{labels: demoComparisonData.map(d=> d.scenario),datasets: [{label: 'Survival Rate(%)',data: demoComparisonData.map(d=> d.survival),backgroundColor: ['#4caf50','#f44336']}]},options:{responsive: true,scales:{y:{beginAtZero: true,max: 100}}}});});