//----------KPI data----------const kpis=[{label:"COâ‚‚ ppm",value:"427",icon:"fa-wind",trend:"+2.8/yr",direction:"up",color:""},{label:"temp anomaly",value:"+1.48Â°C",icon:"fa-temperature-high",trend:"+0.02Â°",direction:"up"},{label:"sea ice extent",value:"4.2M kmÂ²",icon:"fa-snowflake",trend:"-8%",direction:"down"},{label:"forest loss",value:"3.7M ha",icon:"fa-tree",trend:"+12%",direction:"up"}];function renderKPI(){const container=document.getElementById('kpiRow');container.innerHTML=kpis.map(k=> ` <div class="kpi-card"> <div class="kpi-icon"><i class="fas ${k.icon}"></i></div> <div class="kpi-content"> <h3>${k.label}</h3> <div class="kpi-value">${k.value}</div> <div class="kpi-trend ${k.direction==='up'?'trend-up':'trend-down'}"> <i class="fas fa-arrow-${k.direction}"></i> ${k.trend}</div> </div> </div>`).join('');}// gauges(simulated)const gauges=[{label:"ocean health",value:"63",unit:"%",icon:"fa-water"},{label:"biodiversity intactness",value:"58",unit:"%",icon:"fa-paw"},{label:"renewable share",value:"29",unit:"%",icon:"fa-solar-panel"}];function renderGauges(){const container=document.getElementById('gaugesRow');container.innerHTML=gauges.map(g=> ` <div class="gauge-card"> <div class="gauge-title"><i class="fas ${g.icon}"></i> ${g.label}</div> <div class="gauge-number">${g.value}<span class="gauge-unit">${g.unit}</span></div> </div>`).join('');}// region rows const regions=[{name:"Amazonia",air:"moderate",water:"high",bio:"critical",airClass:"warning",waterClass:"critical",bioClass:"critical"},{name:"Congo Basin",air:"good",water:"moderate",bio:"warning",airClass:"good",waterClass:"warning",bioClass:"warning"},{name:"South Asia",air:"critical",water:"critical",bio:"warning",airClass:"critical",waterClass:"critical",bioClass:"warning"},{name:"Arctic",air:"good",water:"good",bio:"warning",airClass:"good",waterClass:"good",bioClass:"warning"},{name:"Europe",air:"moderate",water:"good",bio:"moderate",airClass:"warning",waterClass:"good",bioClass:"warning"},];function renderRegions(){const container=document.getElementById('regionRows');container.innerHTML=regions.map(r=> ` <div class="table-row"> <span><i class="fas fa-map-pin"></i> ${r.name}</span> <span><span class="status-badge ${r.airClass}">${r.air}</span></span> <span><span class="status-badge ${r.waterClass}">${r.water}</span></span> <span><span class="status-badge ${r.bioClass}">${r.bio}</span></span> </div>`).join('');}// charts function initCharts(){const ctxTemp=document.getElementById('tempChart').getContext('2d');new Chart(ctxTemp,{type: 'line',data:{labels: ['1980','1990','2000','2010','2020','2026'],datasets: [{label: 'anomaly Â°C',data: [0.2,0.4,0.6,0.9,1.2,1.48],borderColor: '#ffb86b',backgroundColor: '#ffb86b20',tension: 0.2,fill: true}]},options:{responsive: true,maintainAspectRatio: false,plugins:{legend:{display: false}},scales:{y:{grid:{color: '#306080'}}}}});const ctxCO2=document.getElementById('co2Chart').getContext('2d');new Chart(ctxCO2,{type: 'line',data:{labels: ['1980','1990','2000','2010','2020','2026'],datasets: [{label: 'COâ‚‚ ppm',data: [338,354,369,389,412,427],borderColor: '#d0f0b0',backgroundColor: '#b0f0b020',tension: 0.2,fill: true}]},options:{responsive: true,maintainAspectRatio: false,plugins:{legend:{display: false}}}});}window.openReport=()=> document.getElementById('reportModal').classList.add('active');window.closeModal=()=> document.getElementById('reportModal').classList.remove('active');window.downloadDemo=()=> alert('ðŸ“¥ planetary report download started(demo).');// back to top const topBtn=document.getElementById('backToTop');window.addEventListener('scroll',()=>{window.scrollY>500 ? topBtn.classList.add('visible'): topBtn.classList.remove('visible');});topBtn.onclick=()=> window.scrollTo({top:0,behavior:'smooth'});// escape modal document.addEventListener('keydown',(e)=>{if(e.key==='Escape')closeModal();});window.onclick=(e)=>{if(e.target===document.getElementById('reportModal'))closeModal();};// initial renders renderKPI();renderGauges();renderRegions();initCharts();